INSERT INTO `product_category_goldbook` (`id`, `product_category_id`, `content`, `validated`, `date_begining`, `date_ending`, `date_add`, `date_update`, `date_delete`) VALUES
(3, 1, 'Accueil chaleureux, nous avons &eacute;t&eacute; merveilleusement accueillis. Le petit d&eacute;jeuner est exceptionnellement bon et le choix important. Les lits sont confortable, et l''on dort comme des b&eacute;b&eacute;s. On en oublierait presque que nous venons pour skier et donc nous lever t&ocirc;t. Nous ne regrettons rien et sommes tr&egrave;s press&eacute;s de revenir. Merci Fr&eacute;d&eacute;rique et Fran&ccedil;ois et &agrave; tr&egrave;s bient&ocirc;t !', 0, NULL, NULL, '2013-03-23 10:47:34', NULL, NULL),
(4, 1, 'Bonjour,\r\nJe tenais &agrave; nouveau &agrave; vous remercier pour votre accueil et votre gentillesse. Nous avons &eacute;t&eacute; enchant&eacute;s de trouver un h&eacute;bergement de cette qualit&eacute;. Nous avons  mon mari et moi l''habitude de nous d&eacute;placer, votre chambre d''h&ocirc;te est un r&eacute;el coup de coeur. J''ai donn&eacute; votre d&eacute;pliant &agrave; mes coll&egrave;gues et ne manquerais pas de vous recommander aupr&egrave;s de nos amis.\r\nNous reviendrons vous voir, c''est certain.\r\n \r\nCordialement\r\n\r\n', 0, '2013-05-04 00:00:00', '2013-07-04 00:00:00', '2013-04-08 12:22:03', NULL, NULL);
